// page init
jQuery(function(){
	jcf.customForms.replaceAll();
	initTouchNav();
	initSlideShow();
	initAccordion();
	initPopups();
	initBackgroundResize();
	initInputs();
	initSameHeight();
	initDoubleClick();
	initSidebarCheck();
});

function initSidebarCheck() {
	var twocolumns = document.getElementById('twocolumns');
	var sidebar = document.getElementById('sidebar');

	if(!sidebar && twocolumns) twocolumns.className += ' full-width';
}

function initDoubleClick() {
	var isTouchDevice = /MSIE 10.*Touch/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	jQuery('.double-click').each(function() {
		var curObj = jQuery(this).attr('readonly', 'readonly');
		var curText = curObj.prev('.input-placeholder-text');
		var curParent = curObj.parent();
		
		curParent.bind(isTouchDevice ? 'touchstart MSPointerDown' : 'dblclick', function(e) {
			setTimeout(function() {
				curObj.removeAttr('readonly').focus();
				curText.hide();
			}, 100);
		});
	});
}


// fade gallery init
function initSlideShow() {
	jQuery('div.visual').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		event: 'click',
		autoRotation: true,
		autoHeight: true,
		switchTime: 4000,
		animSpeed: 500
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.container-sort:has(.sort-list)').slideAccordion({
		opener: 'a.opener, .btn-close',
		slider: '.slide',
		animSpeed: 300,
		items: '.sort-list > li:has(.slide)'
	});
}

// popups init
function initPopups() {
	jQuery('#nav').contentPopup({
		mode: 'click',
		popup: 'ul',
		btnOpen: '.opener'
	});
	jQuery('.action-list>li').contentPopup({
		mode: 'click',
		popup: '.dropdown-box',
		btnOpen: '.opener',
		btnClose: '.btn-close'
	});
}

// stretch background to fill blocks
function initBackgroundResize() {
	jQuery('.slide').each(function() {
		ImageStretcher.add({
			container: this,
			image: '.visual-decor'
		});
	});
}

// clear inputs on focus
function initInputs() {
	PlaceholderInput.replaceByOptions({
		// filter options
		clearInputs: true,
		clearTextareas: true,
		clearPasswords: true,
		skipClass: 'default',
		showPasswordBullets: true,
		
		// input options
		wrapWithElement: true,
		showUntilTyping: true,
		getParentByClass: false,
		placeholderAttr: 'placeholder'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.box-container').sameHeight({
		elements: '.box>.holder',
		useMinHeight: true,
		flexible: true,
		multiLine: true
	});
	jQuery('.thumbnails').sameHeight({
		elements: '.text-holder',
		flexible: true,
		multiLine: true
	});
	jQuery('.sort-columns').sameHeight({
		elements: '.column',
		flexible: true,
		multiLine: true
	});
	jQuery('.container-columns, #sidebar').sameHeight({
		elements: '.heading-holder',
		flexible: true,
		multiLine: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

